mapper.select:
	SELECT * FROM (
		SELECT rownum rnum, B.*  FROM (
			SELECT b.mboard_no, b.member_no, b.mboard_title, b.mboard_content, b.mboard_date, b.mboard_hit, b.mboard_cmtcnt,
				m.member_id, m.member_nick 
			FROM mboard b
			JOIN member m ON ( b.member_no = m.member_no ) ORDER BY mboard_no DESC
		) B
	) MBOARD
 	WHERE rnum between #{startNo } AND #{endNo }

mapper.update:
UPDATE mboard
SET mboard_hit = mboard_hit + 1
WHERE mboard_no = #{mboardNo }

mapper.select:

SELECT fileUpload_no, mboard_no, fileUpload_ori, fileUpload_stor
FROM FileUpload
WHERE mboard_no = #{mboardNo }

SELECT fileUpload_no, mboard_no, fileUpload_ori, fileUpload_stor
FROM FileUpload
WHERE fileUpload_no = #{fileUploadNo }

mapper.update:
UPDATE mboard
SET mboard_title = #{mboardTitle },
	mboard_content = #{mboardContent }
WHERE mboard_no = #{mboardNo }
