mapper.select:
	SELECT * FROM (
		SELECT rownum rnum, Mboard  FROM (
			SELECT mboard_no, mboard_title, mboard_content, mboard_date, mboard_hit, mboard_cmtcnt,
				member.member_id, member.member_nick 
			FROM mboard 
			JOIN member m = ON ( mboard.member_no = member.member_no ) ORDER BY mboard_no DESC
		) M
	) MBOARD
 	WHERE rnum between #{startNo } AND #{endNo }

SELECT mboard_no, mboard_title, mboard_content
FROM mboard
WHERE mboard_no = #{mboardNo}
