mapper.select:
SELECT count(*) FROM ( SELECT rownum rnum, B.* FROM ( SELECT n.mtboard_no, m.member_nick, n.field, n.jobduty, n.jobgroup, n.career , n.mtboard_title, n.mtboard_content , n.mtboard_cmcnt , n.mtboard_hit , n.mtboard_date, n.co_name, n.mt_price , ( SELECT count(*) FROM mtmark MK WHERE N.mtboard_no = MK.mtboard_no ) mark_cnt FROM mtboard n JOIN member m ON ( n.member_no = m.member_no ) WHERE 1=1
AND ${type} like '%' || #{keyword} || '%'
AND mtboard_title like '%' || #{keyword} || '%' OR mtboard_content like '%' || #{keyword} || '%'
AND field = #{field}
) B ) MtBoard

SELECT * FROM ( SELECT rownum rnum, B.* FROM ( SELECT n.mtboard_no, m.member_nick, n.field, n.jobduty, n.jobgroup, n.career , n.mtboard_title, n.mtboard_content , n.mtboard_cmcnt , n.mtboard_hit , n.mtboard_date, n.co_name, n.mt_price , ( SELECT count(*) FROM mtmark MK WHERE N.mtboard_no = MK.mtboard_no ) mark_cnt FROM mtboard n JOIN member m ON ( n.member_no = m.member_no ) WHERE 1=1
AND ${type} like '%' || #{keyword} || '%'
AND mtboard_title like '%' || #{keyword} || '%' OR mtboard_content like '%' || #{keyword} || '%'
AND field = #{field}
ORDER BY ${sort} DESC ) B ) MtBoard WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE mtboard
SET mtboard_hit = mtboard_hit + 1
WHERE mtboard_no = #{mtboardNo}

mapper.insert:

	SELECT mtboard_seq.nextval FROM dual		


INSERT INTO mtboard (
	mtboard_no, member_no, field, jobduty, jobgroup, career
	, mtboard_title, mtboard_content, mtboard_hit
	, mtboard_date, co_name, mt_price
)

VALUES (
	#{mtboardNo }, #{memberNo }, #{field }, #{jobDuty }, #{jobGroup }
	, #{career }, #{mtboardTitle }, #{mtboardContent }, #{mtboardHit }
	, sysdate, #{coName}, #{mtPrice}	
)

INSERT INTO fileUpload (
	fileUpload_no, mtboard_no, fileUpload_ori, fileUpload_stor )

VALUES ( fileUpload_seq.nextval, #{mtBoardNo }
		, #{fileUploadOri }, #{fileUploadStor }
)

