mapper.update:
	UPDATE mtboard
   	SET    co_name = #{coName}
    	 , mt_price = #{mtPrice}
	     , mtboard_title = #{mtboardTitle}
		 , mtboard_content = #{mtboardContent}
	WHERE mtboard_no = #{mtboardNo}

mapper.select:
SELECT count(*)
FROM mtmark
WHERE mtboard_no = #{mtboardNo}
	AND member_no = #{memberNo }

SELECT count(*)
FROM mtmark
WHERE member_no = #{memberNo }

mapper.delete:
DELETE mtmark
WHERE member_no = #{memberNo }
	AND mtboard_no = #{mtboardNo}
